
public class Addition2 {
	//function with parameters
	static void addition(int x,int y) {
		int res;
		res = x + y;
		System.out.println("sum of two numbers are : " + res);
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		addition(40,40); 
		addition(20,40);
		addition(30,40);
	}
	
}
